package com.star;

public class StarA {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("printing pattern");
		
		for( int i= 1;i<=4;i++)
		{
			for(int j=1;j<=i;j++)
			{
				
					System.out.print(" * ");
				
			}
			System.out.println(" ");
		}
	}

}
